// IF WE HAVE AN ACTOR TO MOVE, JUST MOVE IT

if(target.localState.actorToMove) {
    let moveTarget = target.localState.actorToMove;
    let grid = nearest("grid",moveTarget.position);
    moveTarget.position = grid.gridPosition(data.pickedPoint);
    //moveTarget.position = data.pickedPoint;
    target.localState.actorToMove = null;
    return;
}

// SET CAMERA COLLISION ELLIPSOID

let grid;

if(target.state.grid) {
    grid = actor(target.state.grid);
} else {
    grid = nearest("grid",data.pickedPoint);
}
camera.ellipsoid = new BABYLON.Vector3(grid.gridSize/2, grid.gridSize/2, grid.gridSize/2);
camera.checkCollisions = true;

// SHOW HUD

target.showHUD([
    {type:"button",label:"Add Tog",script:"Maps/Add Tog"},
    {type:"button",label:"Grid Options",script:"Maps/Grid/Grid Options"}
    ],data);
