// ROTATE 45 DEG

quarterRot = Math.PI/4;
let currentRotation = target.rotation.y;

// INCREMENT 

currentRotation += quarterRot;

// ROUND TO QUARTER ROTATION

currentRotation = Math.round(currentRotation / quarterRot) * quarterRot;

if(currentRotation > (Math.PI*7)/4) {
    currentRotation = 0;
}
target.rotation.y = currentRotation;
