// PLACE AT CLICKED POINT

tog.position = data.pickedPoint;

// SAVE IMAGE PATH WITH TOG PROXY

tog.setState({portrait:imagePath});

// USED CREATED EVENT TO BUILD ACTUAL TOG

tog.on("placed","Togs/Build Tog");

// ALSO TRIGGER THE EVENT

tog.trigger("placed");
