// GET IMAGE FROM WEB

let image = fetchFileDialog("maps");

// LOAD A MAP

let map = texture(image);
let width = map._texture.width;
let height = map._texture.height;

// CREATE GROUND

let ground = primitive("CreateGround", 'ground',{width:width, height:height,subdivisions:16});
ground.position.y = 0;
ground.checkCollisions = true;

// SET MAP TEXTUE

ground.diffuseTexture = map;

// CREATE GRID

let mapGrid = grid(ground,width,height,10);
ground.setState({grid:mapGrid.id});

// SETUP PICK

ground.on("pick","Maps/Pick Map");
